// Prend le premier item, qui contient la propriété 'text' (un tableau d'objets)
const allTextObjects = $input.first().json.text;
// Pour chaque sous-objet, récupère le champ 'text'
const mergedText = allTextObjects.map(obj => obj.text).join('\n\n---\n\n');
return [{ json: { text: mergedText } }];
